import { useEffect, useCallback } from "react";
import "./Admin.css";

const Admin = ({ patientData, setPatientData, setUserState }) => {

   const updatePatientData = useCallback(async (patientCode) => {
      try {
         const response = await fetch("http://localhost:4000/patient/all");
         const data = await response.json();
         setPatientData(data);
      } catch (error) {
         console.log(error);
      }
   }, [setPatientData]);

   useEffect(() => {
      updatePatientData();
   }, [updatePatientData]);

   const logoutHandler = () => {
      localStorage.removeItem("triage-admin-username");
      setUserState("SignedOut");
   }

   const admitHandler = (patientId) => {
      try {
         fetch("http://localhost:4000/patient/admit", {
            method: "POST",
            headers: {
               "Content-Type": "application/json"
            },
            body: JSON.stringify({
               patientId: patientId
            })
         });
         updatePatientData();
      } catch (error) {
         console.log(error);
      }
   }

   console.log(patientData);

   return (
      <div className="admin-container">
         <h2>Viewing Patient Data ({localStorage.getItem("triage-admin-username")})</h2>
         <ul>
            {patientData.map((patient, index) => {
               return (
                  <li key={index}>
                     <div>
                        <p>Name: {patient.patient_name}</p>
                        <p>Code: {patient.patient_code}</p>
                        <p>Severity: {patient.severity}</p>
                        <p>Wait Time: {patient.wait_time}</p>
                     </div>
                     <button className="admit-button" onClick={() => admitHandler(patient.patient_id)} >Admit</button>
                  </li>
               );
            })}
         </ul>
         <button onClick={logoutHandler}>Log Out</button>
      </div>
   );
}

export default Admin;
