import { useState } from "react";
import "./Login.css";

const PATIENT = "patient";
const ADMIN = "admin";

const Login = (props) => {

   const [loginUser, setLoginUser] = useState(PATIENT);

   let title;
   let form;
   let otherLoginUser;
   if (loginUser === PATIENT) {
      title = "Check Your Wait Time";
      form = (
         <form>
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" name="name" required />
            <label htmlFor="code">3-Letter Code:</label>
            <input type="text" id="code" name="code" required />
            <button type="submit" className="light-blue border-radius-sm">
               Check Wait Time
            </button>
         </form>
      );
   } else if (loginUser === ADMIN) {
      title = "View All Patient Data";
   }

   return (
      <section class="form-container border-radius-lg">
         <h2>{title}</h2>
         {form}
         <button>Login As </button>
      </section>
   );
};

export default Login;
